/* global expect */
'use strict';

describe('Main', () => {

  it('should export all classes', () => {
    const index = require('src/index.js');
    expect(Object.keys(index)).to.have.lengthOf(6);
    expect(index.BotCore).to.exist;
    expect(index.BotDomain).to.exist;
    expect(index.BotBaseActions).to.exist;
    expect(index.BaseConnector).to.exist;
    expect(index.FacebookConnector).to.exist;
    expect(index.RestConnector).to.exist;
  });

});
